import React from 'react';
import './ElementPusher.css';

class ElementPusher extends React.Component {
	render () {
		return (
			<div>
			<input className="push-textfield" ref="pushTextfield" placeholder="Insert item's name" onKeyDown={() => this.handlePushBtnPressed()}/>
			<button className="push-button" onClick={() => this.handlePushBtnPressed()}>Add task</button>
			</div>
		);
	}

		handlePushBtnPressed(event)
		{
			if(event.key ==="Enter")
			{
				this.props.onClick(this.refs.pushTextfield.value); this.refs.pushTextfield.value='';
			}
		}

}

export default ElementPusher;
