import React from 'react';
import './Container.css';
import ElementPusher from './ElementPusher'
import ItemsList from './ItemsList'
import Item from './Item'
const uuidv4 = require('uuid/v4');

class Container extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			items: [],
		};
	}

	render () {
		return (
			<div className="w3-container">
				<div className="w3-container">
					<ElementPusher onClick={(name) => this.handleAddElement(name)}/>
				</div>
				<div>
					<ItemsList items={this.state.items} sortByPriority={(order) => this.handleSortByPriority(order)} sortByDate={(order) => this.handleSortByDate(order)} sortByName={(order) => this.handleSortByName(order)}/>
				</div>
			</div>
		);
	}

	handleSortByName(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.name > b.name) ? 1 : ((b.name > a.name) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	handleSortByDate(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.date > b.date) ? 1 : ((b.date > a.date) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	handleSortByPriority(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.priority > b.priority) ? 1 : ((b.priority > a.priority) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	removeElement(uuid) {
		let items = [...this.state.items];
		items.splice(items.map(e => {return e.key}).indexOf(uuid),1);
		this.setState({items: items});
	}

	changeItemName(name,key) {
		let items = [...this.state.items];
		items.map((x => x.key === key && (x.name = name)).bind({name:name,key:key}));
		this.setState({items:items});
	}

	handleChangePriority(priority,key) {
		let items = [...this.state.items];
		items.map( (x => x.key === key && (x.priority = priority)).bind({priority: priority, key: key}));
		this.setState({items:items});
	}

	handleCompleteTask(key) {
		let items = [...this.state.items];
		items.map((x => x.key === key && (x.completed = !x.completed)).bind({key: key}));
		this.setState({items:items});
	}

	newItemInstance(uuid,name,date,completed,priority) {
		return (
			<Item changeName={(name) => this.changeItemName(name,uuid)} changePriority={(priority) => this.handleChangePriority(priority,uuid)} completeTask={() => this.handleCompleteTask(uuid)} removeElement={() => this.removeElement(uuid)} completed={completed} name={name} date={date} key={uuid}/>
		);
	}

	handleAddElement(name) {
		if(name && name.length>0) {
			let items = [...this.state.items];
			let uuid = uuidv4();
			let date = new Date();

			items.push({
				instance: this.newItemInstance(uuid,name,date,false,"0"),
				key: uuid,
				date: date,
				name: name,
				priority: "0",
				completed: false,
			});

			this.setState({items: items});
		}
	}
}

export default Container;
