import React from 'react';
// import './Item.css';

class Item extends React.Component {

	constructor(props) {
		super(props);

		// this.state = {
		// 	name: props.name,
		// 	date: props.date,
		// 	priority: '0',
		// }
	}

	render() {
		return (
			<tr>
				<td>
						<input className="w3-input item-name" defaultValue={this.props.name} ref="itemNameTextfield" onBlur={() => this.props.name =  this.refs.itemNameTextfield.value}/>
				</td>
				<td>
					{this.props.date.toLocaleDateString("it-IT")}
				</td>
				<td>
					{this.props.date.toLocaleTimeString("it-IT")}
				</td>
				<td>
					<select className="w3-select" ref="itemPrioritySelect" value={this.props.priority} onChange={() => this.setState({priority: this.refs.itemPrioritySelect.value})}>
						<option value="0">--</option>
						<option value="1">Low</option>
						<option value="2">Medium</option>
						<option value="3">High</option>
					</select>
				</td>
				<td>
					<button className="w3-btn w3-blue" onClick={this.props.removeElement}>Done</button>
				</td>

			</tr>
		);
	}
}

export default Item;
