import React from 'react';
// import './Item.css';

class Item extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			name: props.name,
			date: props.date,
		}
	}

	render() {
		return (
			<tr>
				<td>
						<input value={this.state.name} ref="ItemNameTextfield" onChange={() => this.state.name=this.refs.ItemNameTextfield.value}/>
				</td>
				<td>
					{this.state.date.toLocaleDateString("it-IT")}
				</td>
				<td>
					{this.state.date.toLocaleTimeString("it-IT")}
				</td>
				<td>
					<button onClick={this.props.removeElement}>Done</button>
				</td>

			</tr>
		);
	}
}

export default Item;
