import React from 'react';
import './Container.css';
import ElementPusher from './ElementPusher'
import ItemsList from './ItemsList'
const uuidv4 = require('uuid/v4');

class Container extends React.Component {

	static notifyText = '';
	static showNotify = false;
	static notifyTimeoutFunction;

	constructor(props) {
		super(props);

		this.state = {
			items: [],
			instances : [],
		};
	}

	render () {
		return (
			<div className="w3-container">
				<div class={(Container.showNotify ? "w3-animate-opacity" : "hidden-opacity") + " w3-panel w3-margin w3-display-bottomleft w3-pale-green w3-border w3-border-green overlay " }>
				  <p>{Container.notifyText}</p>
				</div>
				<div className="w3-container">
					<ElementPusher onClick={(name) => this.handleAddElement(name)} notify={(text) => this.handleNotify(text)}/>
				</div>
				<div>
					<ItemsList swapItems={(key1,key2) => this.handleSwapItems(key1,key2)} removeElement={(uuid) => this.removeElement(uuid)} changePriority={(priority,uuid) => this.handleChangePriority(priority,uuid)} changeItemName={(name,uuid) => this.changeItemName(name,uuid)} completeTask={(uuid) => this.handleCompleteTask(uuid)} items={this.state.items} sortByPriority={(order) => this.handleSortByPriority(order)} sortByDate={(order) => this.handleSortByDate(order)} sortByName={(order) => this.handleSortByName(order)}/>
				</div>
			</div>
		);
	}

	handleNotify(text) {
		Container.notifyText = text;
		Container.showNotify = true;

		if(Container.notifyTimeoutFunction)
			clearTimeout(Container.notifyTimeoutFunction)

	 Container.notifyTimeoutFunction =	setTimeout(function(){
			Container.showNotify = false;
             this.setState({});
        }.bind(this),3 * 1000);
	}

	handleSwapItems(key1,key2) {
		let items = [...this.state.items];
		let firstIndex = items.map(x => x.key).indexOf(key1);
		let secondIndex = items.map(x => x.key).indexOf(key2);
		items[firstIndex] = items.splice(secondIndex, 1, items[firstIndex])[0];
		this.setState({items: items});
		this.handleNotify('Items swapped');
	}

	handleSortByName(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.name > b.name) ? 1 : ((b.name > a.name) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	handleSortByDate(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.date > b.date) ? 1 : ((b.date > a.date) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	handleSortByPriority(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.priority > b.priority) ? 1 : ((b.priority > a.priority) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
	}

	removeElement(uuid) {
		let items = [...this.state.items];
		items.splice(items.map(e => {return e.key}).indexOf(uuid),1);
		this.setState({items: items});
	}

	changeItemName(name,key) {
		let items = [...this.state.items];
		items.map((x => x.key === key && (x.name = name)).bind({name:name,key:key}));
		this.setState({items:items});
	}

	handleChangePriority(priority,key) {
		let items = [...this.state.items];
		items.map( (x => x.key === key && (x.priority = priority)).bind({priority: priority, key: key}));
		this.setState({items:items});
	}

	handleCompleteTask(key) {
		let items = [...this.state.items];
		items.map((x => x.key === key && (x.completed = !x.completed)).bind({key: key}));
		this.setState({items:items});
	}

	handleAddElement(name) {
		if(name && name.length>0) {
			let items = [...this.state.items];
			let uuid = uuidv4();
			let date = new Date();

			items.push({
				key: uuid,
				date: date,
				name: name,
				priority: "0",
				completed: false,
			});

			this.setState({items: items});
		}
	}
}

export default Container;
