import React from 'react';
import './ElementPusher.css';

class ElementPusher extends React.Component {

	render () {
		return (
			<div>
			<input className="push-textfield" ref="pushTextfield.props.children" placeholder="Insert item's name"/>
				<button className="push-button" onClick={() => this.handleAddElement()}>Add item</button>
			</div>
		);
	}

	handleAddElement() {
		console.log(this.refs.pushTextfield.props.children)
	}
}

export default ElementPusher;
