import React from 'react';
import { Redirect  } from "react-router-dom";
var SHA256 = require("crypto-js/sha256");
// console.log(SHA256("password").toString());

class Login extends React.Component {

	static isValidLogin = false;
	static sessionId;

	handleLogin(username,password) {
		fetch('/login',{
			 method: 'post',
			 headers: {'Content-Type':'application/json'},
			 body: JSON.stringify({
				"username": username,
				"password": SHA256(password).toString()
			})
		})
		.then(res => res.json())
		.then(result => {
			Login.isValidLogin = result.valid;
			Login.sessionId = result.sessionId;
			console.log(	Login.sessionId)
			this.setState({});
		});

	}

	render() {
		if(Login.isValidLogin) {
			return (<Redirect to="/"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input ref="usernameInput" type="text"/>
	         </label>

					 <label>
						 password:
						 <input ref="passwordInput" type="password" />
					 </label>
	         <button onClick={() => this.handleLogin(this.refs.usernameInput.value,this.refs.passwordInput.value)}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
