import React from 'react';
import SessionManager from './SessionManager'
import { Redirect  } from "react-router-dom";
var SHA256 = require("crypto-js/sha256");

global.isValidLogin=false;
global.sessionId;

class Login extends React.Component {

	// static isValidLogin = false;
	constructor(props) {
		super(props);

	}

	handleLogin(username,password) {
		fetch('/login',{
			 method: 'post',
			 headers: {'Content-Type':'application/json'},
			 body: JSON.stringify({
				"username": username,
				"password": SHA256(password).toString()
			})
		})
		.then(res => res.json())
		.then(result => {
			this.context.isValidLogin = result.valid;
			this.context.sessionId = result.sessionId;
			// console.log(SessionManager.sessionId)
			this.setState({});
		});

	}

	render() {
		if(this.context.isValidLogin) {
			return (<Redirect to="/home"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input ref="usernameInput" type="text"/>
	         </label>

					 <label>
						 password:
						 <input ref="passwordInput" type="password" />
					 </label>
	         <button onClick={() => this.handleLogin(this.refs.usernameInput.value,this.refs.passwordInput.value)}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
