import React from 'react';
import { Redirect  } from "react-router-dom";
var SHA256 = require("crypto-js/sha256");
// console.log(SHA256("password").toString());

class Login extends React.Component {

	static isValidLogin=false;

	handleLogin(username,password) {
		let p = SHA256(password).toString();
		let u = username;
		fetch('/login',{
			 method: 'post',
			 headers: {'Content-Type':'application/json'},
			 body: JSON.stringify({
				"username": u,
				"password": p
			})
		})
		.then(res => console.log(res.json().valid))
		// .then(result => Login.isValidLogin = result.valid);

		// console.log(Login.isValidLogin)
		this.setState({})
	}

	render() {
		// this.setState({ok:false})
		//
		// console.log(Login.isValidLogin)
		if(Login.isValidLogin) {
			return (<Redirect to="/"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input ref="usernameInput" type="text"/>
	         </label>

					 <label>
						 password:
						 <input ref="passwordInput" type="password" />
					 </label>
	         <button onClick={() => this.handleLogin(this.refs.usernameInput.value,this.refs.passwordInput.value)}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
