import React from 'react';
import './style/Login.css';

import { Redirect  } from "react-router-dom";
var SHA256 = require("crypto-js/sha256");

class Login extends React.Component {

	handleLogin(username,password) {
		fetch('/login',{
			 method: 'post',
			 headers: {'Content-Type':'application/json'},
			 body: JSON.stringify({
				"username": username,
				"password": SHA256(password).toString()
			})
		})
		.then(res => res.json())
		.then(result => {
			sessionStorage.setItem('isValidLogin',result.valid);
			sessionStorage.setItem('sessionId',result.sessionId);
			this.setState({});
		});

	}

	render() {
		console.log('login')
		if(sessionStorage.getItem('isValidLogin') === "true" && sessionStorage.getItem('sessionId') !== "undefined") {
			return (<Redirect to="/home"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input ref="usernameInput" type="text"/>
	         </label>

					 <label>
						 password:
						 <input ref="passwordInput" type="password" />
					 </label>
	         <button onClick={() => this.handleLogin(this.refs.usernameInput.value,this.refs.passwordInput.value)}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
