import React from 'react';
import './ElementPusher.css';

class ElementPusher extends React.Component {
	render () {
		return (
			<div>
			<input className="push-textfield" ref="pushTextfield" placeholder="Insert item's name" onKeyPress={() => this.handlePushBtnPressed(event)}/>
			<button className="push-button" onClick={() => this.handlePushBtnPressed({key: 13})}>Add task</button>
			</div>
		);
	}

		handlePushBtnPressed()
		{
			this.props.onClick(this.refs.pushTextfield.value); this.refs.pushTextfield.value='';
		}

}

export default ElementPusher;
