import { Redirect  } from "react-router-dom";

class Login extends React.Component {
	constructor(props)
	{
		super(props);
		this.state = {isValidLogin: false};
	}

	handleLogin() {
		console.log('login')
	}

	componentDidMount() {
		if(this.state.isValidLogin) {
			setState()
		}
	}

	render() {
		// this.setState({ok:false})

		console.log(this.state.isValidLogin)
		if(this.state.isValidLogin) {
			return (<Redirect to="/home"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input type="text"/>
	         </label>

					 <label>
						 password:
						 <input type="text" />
					 </label>
	         <button onClick={() => this.handleLogin()}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
