import React from 'react';
import './ElementPusher.css';
import ItemsList from './ItemsList'

class ElementPusher extends React.Component {

	render () {
		return (
			<div>
			<input className="push-textfield" ref="pushTextfield" placeholder="Insert item's name"/>
				<button className="push-button" onClick={() => this.props.onClick()}>Add item</button>
			</div>
		);
	}

	handleAddElement() {
		addElement(this.refs.pushTextfield.value)
	}
}

export default ElementPusher;
