import React from 'react';
// import './Item.css';

class Item extends React.Component {
	render() {
		return (
			<tr>
				<td>
						<input value={props.name} ref="ItemNameTextfield" onChange={() => props.name=this.refs.ItemNameTextfield.value}/>
				</td>
				<td>
					{props.date.toLocaleDateString("it-IT")}
				</td>
				<td>
					{props.date.toLocaleTimeString("it-IT")}
				</td>
				<td>
					<button onClick={props.removeElement}>Done</button>
				</td>

			</tr>
		);
	}
}

export default Item;
