import React from 'react';
import './ElementPusher.css';

class ElementPusher extends React.Component {
	constructor(props) {
		super(props);
	 	this.onSubmit = this.onSubmit.bind(this);
	}
	render () {
		return (
			<div>
			<input className="push-textfield" ref="push-textfield" placeholder="Insert item's name"/>
				<button className="push-button" onClick={(e) => this.onSubmit()}>Add item</button>
			</div>
		);
	}

	onSubmit(e) {
        e.preventDefault();
        var title = this.title;
        console.log(title);
    }
}

export default ElementPusher;
