import React from 'react';
import './Container.css';
import ElementPusher from './ElementPusher'
import ItemsList from './ItemsList'
import Item from './Item'
const uuidv4 = require('uuid/v4');

class Container extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			items: [],
		};
	}

	render () {
		return (
			<div className="w3-container">
				<div className="w3-container">
					<ElementPusher onClick={(name) => this.handleAddElement(name)}/>
				</div>
				<div>
					<ItemsList items={this.state.items} sortByName={() => this.handleSortByName()}/>
				</div>
			</div>
		);
	}

	handleSortByName() {
		objs.sort((a,b) => (a.last_nom > b.last_nom) ? 1 : ((b.last_nom > a.last_nom) ? -1 : 0));
	}

	removeElement(uuid) {
		let items = [...this.state.items];
		items.splice(items.map(e => {return e.key}).indexOf(uuid),1);
		this.setState({items: items});
	}

	handleAddElement(name) {
		if(name && name.length>0) {
			let items = this.state.items.slice();
			let uuid = uuidv4();
			items.push(<Item removeElement={() => this.removeElement(uuid)} name={name} date={new Date()} key={uuid}/>);

			this.setState({items: items});
		}
	}
}

export default Container;
