import React from 'react';
import './style/Login.css';

import { Redirect  } from "react-router-dom";
var SHA256 = require("crypto-js/sha256");

class Login extends React.Component {

	handleLogin(username,password) {
		fetch('/login',{
			 method: 'post',
			 headers: {'Content-Type':'application/json'},
			 body: JSON.stringify({
				"username": username,
				"password": SHA256(password).toString()
			})
		})
		.then(res => res.json())
		.then(result => {
			sessionStorage.setItem('isValidLogin',result.valid);
			sessionStorage.setItem('sessionId',result.sessionId);
			this.setState({});
		});

	}

	render() {
		if(sessionStorage.getItem('isValidLogin') === "true" && sessionStorage.getItem('sessionId') !== "undefined") {
			return (<Redirect to="/home"/>);
		}
		else
			return (
				<div className="w3-container w3-display-middle w3-border w3-border-blue w3-padding w3-white">

					<div className="w3-container w3-border-bottom">
					  <h1 className="w3-text-pink">Login</h1>
					</div>

					<div className="w3-container w3-center">
						<p>
		         <label>Username</label>
						 <input className="login-input" placeholder="Insert username" className="w3-input" ref="usernameInput" type="text"/>
						</p>

						<p>
						 <label>Password</label>
						 <input className="login-input" placeholder="Insert password" className="w3-input" ref="passwordInput" type="password" />
						</p>
		       </div>
					 <div className="w3-container w3-padding w3-center">
					 	<button className="login-button w3-btn w3-blue" onClick={() => this.handleLogin(this.refs.usernameInput.value,this.refs.passwordInput.value)}>Login</button>
					</div>
				</div>
			);
	}
}

export default Login;
