import React from 'react';
import './Container.css';
import ElementPusher from './ElementPusher'
import ItemsList from './ItemsList'
import Item from './Item'
const uuidv4 = require('uuid/v4');

class Container extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			items: [],
		};
	}

	render () {
		return (
			<div className="w3-container">
				<div className="w3-container">
					<ElementPusher onClick={(name) => this.handleAddElement(name)}/>
				</div>
				<div>
					<ItemsList items={this.state.items} sortByName={(order) => this.handleSortByName(order)}/>
				</div>
			</div>
		);
	}

	handleSortByName(order) {
		let items = [...this.state.items];
		items.sort((a,b) => (a.name > b.name) ? 1 : ((b.name > a.name) ? -1 : 0));
		if(order)
			items.reverse();
		this.setState({items: items});
		console.log(items[0])
	}

	removeElement(uuid) {
		let items = [...this.state.items];
		items.splice(items.map(e => {return e.key}).indexOf(uuid),1);
		this.setState({items: items});
	}

	changeItemName(name,key) {
		let items = [...this.state.items];
		items.map((x => x.key === key ? x.name = name : 1).bind({name:name,key:key}));
		this.setState({items:items});
	}

	handleChangePriority(priority,key) {
		let items = [...this.state.items];
		items.map( (x => x.key === key ? x.priority = priority : 1).bind({priority: priority, key: key}));
		this.setState({items:items});
}


	handleAddElement(name) {
		if(name && name.length>0) {
			let items = [...this.state.items];
			let uuid = uuidv4();
			let date = new Date();

			items.push({
				instance: <Item changeName={(name) => this.changeItemName(name,uuid)} changePriority={(priority) => this.handleChangePriority(priority,uuid)} removeElement={() => this.removeElement(uuid)} name={name} date={new Date()} key={uuid}/>,
				key: uuid,
				date: date,
				name: name
			});

			this.setState({items: items});
		}
	}
}

export default Container;
