import React from 'react';
// import Item from './Item'
// import './ItemsList.css';

class ItemsList extends React.Component {

	constructor(props)
	{
		super(props);
		this.state = {
			nameSort: null,
			dateSort: null,
			prioritySort: null,
		}
	}
	render () {
		return (
			<div>
				<table className="w3-table w3-bordered">
					<thead>
						<tr className="w3-cyan">
							<th onClick={() => {this.props.sortByName(this.state.nameSort); this.setState({nameSort: !this.state.nameSort ? true : !this.state.nameSort})}}>Name <span>{this.state.nameSort !== null && (this.state.nameSort ? 	'▼' : '▲')}</span></th>
								<th onClick={() => {this.props.sortByName(this.state.nameSort); this.setState({nameSort: !this.state.nameSort ? true : !this.state.nameSort})}}>Date <span>{this.state.nameSort !== null && (this.state.nameSort ? 	'▼' : '▲')}</span></th>
							<th>Hour</th>
							<th>Priority</th>
							<th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{this.props.items.map(x => x.instance)}
					</tbody>
				</table>
			</div>
		);
	}

}

export default ItemsList;
