import React from 'react';
import { Redirect  } from "react-router-dom";

class Login extends React.Component {

	static isValidLogin=false;

	handleLogin() {
		// fetch('/login',{
		// 	 method: 'post',
		// 	 headers: {'Content-Type':'application/json'},
		// 	 body: JSON.stringify({
		// 		"username": uuid,
		// 		"password": pass
		// 	})
		Login.isValidLogin = true;
		console.log(Login.isValidLogin)
		this.setState({})
	}

	render() {
		// this.setState({ok:false})

		console.log(Login.isValidLogin)
		if(Login.isValidLogin) {
			return (<Redirect to="/"/>);
		}
		else
			return (
			<div>
				<p>login page</p>
				<div>
	         <label>
	           username:
	           <input type="text"/>
	         </label>

					 <label>
						 password:
						 <input type="text" />
					 </label>
	         <button onClick={() => this.handleLogin()}>submit</button>
	       </div>
			</div>
			);
	}
}

export default Login;
